#       define RC_ONE   (1ULLä½¿ç”¨is-aæŒ‡é’ˆè®°å½•å¼•ç”¨è®¡æ•°æ—¶çš„æºç  ï¼ˆä»£ç æœ‰ä¿®æ”¹ï¼Œæºç åœ¨NSObject.mmä¸­æ‰¾ï¼‰
id objc_object::rootRetain(bool tryRetain, bool handleOverflow) {
    isa_t oldisa;
    isa_t newisa;

    transcribeToSideTable = false;
    oldisa = LoadExclusive(&isa.bits);
    newisa = oldisa;
    if (newisa.indexed) {
        uintptr_t carry;
        newisa.bits = addc(newisa.bits, RC_ONE, 0, &carry);  // ç»™extra_rc++

        // extra_rc æ— æ³•å‚¨å­˜äº†ï¼Œ
        if (carry) {
            // newisa.extra_rc++ overflowed
            if (!handleOverflow) return rootRetain_overflow(tryRetain);
            // Leave half of the retain counts inline and
            // prepare to copy the other half to the side table.
            if (!tryRetain && !sideTableLocked) sidetable_lock();
            sideTableLocked = true;
            transcribeToSideTable = true;
            newisa.extra_rc = RC_HALF;
            newisa.has_sidetable_rc = true;

            // Copy the other half of the retain counts to the side table.
            sidetable_addExtraRC_nolock(RC_HALF);

            if (!tryRetain && sideTableLocked) sidetable_unlock();
            return (id)this;
        }
    }
}

inline uintptr_t objc_object::rootRetainCount()
{
    sidetable_lock();
    isa_t bits = LoadExclusive(&isa.bits);
    if (bits.indexed) {
        uintptr_t rc = 1 + bits.extra_rc;
        if (bits.has_sidetable_rc) {     // å¼•ç”¨è®¡æ•°å€¼è¿‡å¤§ï¼Œè¿˜éœ€è¦å­˜å‚¨åœ¨sidetableä¸­
            rc += sidetable_getExtraRC_nolock();
        }
        sidetable_unlock();
        return rc;
    }
}
