CFRunLoopRef _CFRunLoopGet0(pthread_t t) {
    if (pthread_equal(t, kNilPthreadT)) {
        t = pthread_main_thread_np(); // t == 0 ä¸ºmain thread
    }

    __CFLock(&loopsLock);
    /// __CFRunLoopsæ˜¯ä¸€ä¸ªCFDictionaryå¯¹è±¡ï¼Œå…¨å±€å­˜å‚¨RunLoopï¼Œkeyä¸ºpthreadï¼Œvalueä¸ºRunloop
    if (!__CFRunLoops) {
        CFMutableDictionaryRef dict = CFDictionaryCreateMutable(kCFAllocatorSystemDefault, 0, NULL, &kCFTypeDictionaryValueCallBacks);
        CFRunLoopRef mainLoop = __CFRunLoopCreate(pthread_main_thread_np());
        CFDictionarySetValue(dict, pthreadPointer(pthread_main_thread_np()), mainLoop);
        if (!OSAtomicCompareAndSwapPtrBarrier(NULL, dict, (void * volatile *)&__CFRunLoops)) {
          CFRelease(dict);
        }
        CFRelease(mainLoop);
    }

    CFRunLoopRef newLoop = NULL;
    // ä»å…¨å±€çš„Dictionaryä¸­å–ç¼“å­˜çš„RunLoopå¯¹è±¡
    CFRunLoopRef loop = (CFRunLoopRef)CFDictionaryGetValue(__CFRunLoops, pthreadPointer(t));
    if (!loop) {
        // å¦‚æœæ²¡æœ‰åˆ™æ–°å»ºRunLoop ã€ä»è¿™é‡Œå¯ä»¥çœ‹å‡ºï¼Œåˆ›å»ºthreadæ—¶å¹¶æ²¡æœ‰å¯¹åº”çš„RunLoopï¼Œåªæœ‰ä¸»åŠ¨CFRunLoopGetCurrentæ‰ä¼šåˆ›å»ºã€‘
        newLoop = __CFRunLoopCreate(t);
        CFDictionarySetValue(__CFRunLoops, pthreadPointer(t), newLoop);
        loop = newLoop;
    }
    __CFUnlock(&loopsLock);

    // don't release run loops inside the loopsLock, because CFRunLoopDeallocate may end up taking it
    if (newLoop) { CFRelease(newLoop); }
    if (pthread_equal(t, pthread_self())) {
        _CFSetTSD(__CFTSDKeyRunLoop, (void *)loop, NULL);
        if (0 == _CFGetTSD(__CFTSDKeyRunLoopCntr)) {
             _CFSetTSD(__CFTSDKeyRunLoopCntr, (void *)(PTHREAD_DESTRUCTOR_ITERATIONS-1), (void (*)(void *))__CFFinalizeRunLoop);
        }
    }

    return loop;
}

