public struct Stack {
    fileprivate var array = [T]()

    public var count: Int {
        return array.count
    }

    public var isEmpty: Bool {
        return array.isEmpty
    }

    public mutating func push(_ element: T) {
        array.append(element)
    }

    public mutating func pop() -> T? {
        return array.popLast()
    }

    public var top: T? {
        return array.last
    }
}

class Stack1 {
    var head: Node1?

    func push(_ element: Element) {
        if head == nil {
            head = Node1(element)
            return
        }

        let node = Node1(element)
        node.next = head
        head = node
    }

    func pop() -> Element? {
        let node = head
        head = head?.next

        return node?.value
    }
}

class Node1 {
    var value: Element
    var next: Node1?

    init(_ v: Element) {
        value = v
    }
}
