func reverseList2(head: Node?) -> Node? {
    var pre = head
    var cur = head?.next

    head?.next = nil
    while let _ = cur {
        let tmp = cur?.next
        cur?.next = pre

        pre = cur
        cur = tmp
    }

    return pre
}

