class Stack1 {
    var head: Node1?

    func push(_ element: Element) {
        if head == nil {
            head = Node1(element)
            return
        }

        let node = Node1(element)
        node.next = head
        head = node
    }

    func pop() -> Element? {
        let node = head
        head = head?.next

        return node?.value
    }
}

class Node1 {
    var value: Element
    var next: Node1?

    init(_ v: Element) {
        value = v
    }
}
