public struct Queue {
    fileprivate var head: Node?
    fileprivate var tail: Node?

    public mutating func enqueue(_ element: T) {
        let node = Node(element)

        if head == nil {
            head = node
        }

        if let tail = tail {
            tail.next = node
        }
        tail = node
    }

    public mutating func dequeue() -> T? {
        guard let head = head else { return nil }

        let node = head
        self.head = head.next

        return node.value
    }
}

class Node {
    var value: T
    var next: Node?

    init(_ v: T) {
        value = v
    }
}
