static IMP addMethod(Class cls, SEL name, IMP imp, const char *types, bool replace)
{
    IMP result = nil;

    method_t *m;
    if ((m = getMethodNoSuper_nolock(cls, name))) {
        // method å·²ç»å­˜åœ¨ï¼Œ already exists  
        if (!replace) {
            // add ä¸åšä»»ä½•å¤„ç†ï¼Œè¿”å›å·²ç»å­˜åœ¨çš„ imp
            result = m->imp;
