        // method è¿˜ä¸å­˜åœ¨ï¼Œåˆ™ä¼šåˆ›å»º ä¸€ä¸ª method_list_tï¼ŒæŠŠ method æ·»åŠ åˆ° method_list_t ä¸­ï¼Œ
        // ç„¶åæŠŠ method_list_t æ·»åŠ åˆ° cls->data()->methods ä¸­(class_rw_t æ‰€æœ‰çš„ class_array_t ä¸­)
        method_list_t *newlist;
        newlist = (method_list_t *)calloc(sizeof(*newlist), 1);
        newlist->entsizeAndFlags = 
            (uint32_t)sizeof(method_t) | fixed_up_method_list;
        newlist->count = 1;
        newlist->first.name = name;
        newlist->first.types = strdup(types);
        if (!ignoreSelector(name)) {
            newlist->first.imp = imp;
        } else {
            newlist->first.imp = (IMP)&_objc_ignored_method;
        }

        prepareMethodLists(cls, &newlist, 1, NO, NO);
        // attachLists ä¼šæ·»åŠ åˆ° methods çš„æœ€å‰é¢ï¼Œæ‰€ä»¥æ¯æ¬¡é¡ºåºä» method_array_t ä¸­å– method_t æ—¶ï¼Œéƒ½ä¼šä¼˜å…ˆå–å‡ºåæ·»åŠ çš„ method_t ã€‚(è¿™é‡Œè§£é‡Šäº† category æ·»åŠ çš„æ–¹æ³•ä¼šä¼˜å…ˆäº class çš„æ–¹æ³•è°ƒç”¨)
        cls->data()->methods.attachLists(&newlist, 1);
        flushCaches(cls);

        result = nil;
    }

    return result;
}

